import { Component, OnInit } from '@angular/core';
import { PassengerService } from '../api/services';
import { FormBuilder } from '@angular/forms';
import { AuthService, User } from '../auth/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register-passenger',
  templateUrl: './register-passenger.component.html',
  styleUrls: ['./register-passenger.component.css']
})
export class RegisterPassengerComponent implements OnInit {

  constructor(private passengerService: PassengerService, private formbuilder: FormBuilder, private authService: AuthService, private router:Router) { }

  PassengerRegistrationForm = this.formbuilder.group({
    email: [''],
    firstName: [''],
    lastName: [''],
    isFemale: [true]
  })

  ngOnInit(): void {
   // throw new Error('Method not implemented.');
  }
  checkPassenger(): void {

    const emailObjectForCheckingifUserExists = { email: this.PassengerRegistrationForm.get('email')?.value?.toString() };
    this.passengerService.findPassenger(emailObjectForCheckingifUserExists).subscribe(p => {
      console.log("passenger Exists logging in");
      this.logIn
    }, error => {
      if (error != 404) {
        console.log(error)
      }
       
    })
    this.passengerService.findPassenger(emailObjectForCheckingifUserExists)
  }


  register() {
    console.log("Form values for registration", this.PassengerRegistrationForm.value);
    // this is a post method so have to populate the body.
   // let serviceObjectEmailOnly: User = { email: this.PassengerRegistrationForm.get('email')?.value }
    this.passengerService.registerPassenger({ body: this.PassengerRegistrationForm.value }).subscribe(p =>



      this.authService.login({ email: this.PassengerRegistrationForm.get('email')?.value }), error => console.log(error))

  }

  private logIn = () => {
    this.authService.login({ email: this.PassengerRegistrationForm.get('email')?.value })
    this.router.navigate(['/search-flights'])
  }

}
<form [formGroup]="PassengerRegistrationForm">


  <div class="container d-grid gap-3" style="max-width: 700px;">

    <div class="form-floating">
      <input (blur)="checkPassenger()" formControlName="email" placeholder="Email" class="form-control" type="text" />
      <label for="email">Email</label>

    </div>
    <div class="d-grid gap-3">

      <div class="form-floating">

        <input formControlName="firstName" class="form-control" type="text" id="firstName" placeholder="First Name" />
        <label class="form-label" for="firstName">First Name</label>

      </div>

      <div class="form-floating">

        <input formControlName="lastName" placeholder="Last Name" class="form-control" id="lastName" type="text" />
        <label class="form-label" for="lastName">Last Name</label>

      </div>


      <div>

        <input formControlName="isFemale" id="isFemale" class="form-check-input" [value]=true type="radio" />
        <label class="ms-2 form-check-label" for="isFemale">Female</label>
      </div>
      <div>
        <input formControlName="isFemale" id="isFemale" class="form-check-input" [value]=false type="radio" />
        <label class="ms-2 form-check-label" for="isFemale">Male</label>
      </div>
    </div>
    <div class="d-flex justify-content-center center">
      <button (click)="register()" type="submit" class="btn btn-primary">Login <i class="fa fa-sign-in" aria-hidden="true"></i></button>
    </div>
  </div>

</form>
