import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FlightService } from '../api/services';
import { FlightRm } from '../api/models';
import { AuthService } from '../auth/auth.service';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})
export class BookFlightComponent implements OnInit {

  constructor(private formbuilder :FormBuilder, private route: ActivatedRoute, private flightService: FlightService, private router: Router, private authService: AuthService) { }

  flightId: string = 'not loaded'
  flightDetailsFromAPIResponse!: FlightRm;

  BookFlightForm = this.formbuilder.group({

    number: [1]
  }
)

  ngOnInit(): void {

    if (this.authService.currentUser == undefined) {

      this.router.navigate(['/register-passenger'])
    }

    this.route.paramMap
      .subscribe(p => this.findFlightForUI(p.get("flightId")))
  }

  private findFlightForUI = (flightId: string | null) => {
    this.flightId = flightId ?? 'not passed';

    this.flightService.findFlight({ id: this.flightId }).subscribe(
      flight => this.flightDetailsFromAPIResponse = flight, this.handleError)


  }
  private handleError = (err: any) => {

    if (err.status == 404) {
      alert("Flight not found!")
      this.router.navigate(['/search-flights'])
    }

    console.log("Response Error. Status: ", err.status)
    console.log("Response Error. Status Text: ", err.statusText)
    console.log(err)
  }
  Book() {

    console.log(`Booking ${this.BookFlightForm.get('number')?.value} passengers for the flight ${this.flightDetailsFromAPIResponse.id} `)
  }

}
<h5 class="text-md-center m-5">Book Flight</h5>

<div class="card-body text-center" style="padding: 20px; background-color:#e5ecf26e">
  <h6 class="card-title ">{{flightDetailsFromAPIResponse.airline}}</h6>
  <div class="card-title" style="color: #ff3939">{{flightDetailsFromAPIResponse.remainingNumberOfSeats}} remaining seats</div>
  <div class="d-grid text-center g-2">
    <div class="row">
      <div class="col">
        <div class="col m-3">

          <i class="text-primary fas fa-plane-departure" style="font-size: 20px;"></i>
        </div>
        <div class="col">
          <i class="fa-solid fa-location-dot">
            {{flightDetailsFromAPIResponse.departure?.place}}
          </i>

        </div>
        <div class="col m-2">
          <i class="fa-regular"></i>
          {{flightDetailsFromAPIResponse.departure?.time | date}}

        </div>
        <div class="col">

          <i class="fa-regular fa-clock">
            {{flightDetailsFromAPIResponse.departure?.time | date:'shortTime'}}
          </i>
        </div>
      </div>

      <div class="col">
        <div style="display: flex;">
          <i class="fa-solid  fa-circle" style="margin-top:51px; font-size: 9px;"></i>
          <hr style="display:block;width: 100%;margin-top: 55px;">
          <i class="fas fa-plane" style="margin-top: 48px;"></i>


        </div>
        <i class="fa fa-usd" aria-hidden="true">{{flightDetailsFromAPIResponse.price}}</i>
        <br>

      </div>
      <div class="col">
        <div class="col m-3">
          <i class="text-primary fas fa-plane-arrival" style="font-size: 20px;"></i>

        </div>
        <div class="col">

          <i class="fa-solid fa-location-dot">
            {{flightDetailsFromAPIResponse.arrival?.place}}
          </i>
        </div>
        <div class="col m-2 text-primary">
          <i class="fa-regular "></i>
          {{flightDetailsFromAPIResponse.arrival?.time | date}}

        </div>
        <div class="col">
          <i class="fa-regular fa-clock">
            {{flightDetailsFromAPIResponse.arrival?.time | date:'shortTime'}}
          </i>

        </div>
      </div>
    </div>
  </div>
</div>

<div class="card-footer d-flex justify-content-center">
  <form [formGroup]="BookFlightForm"  (ngSubmit)="Book()" >
  <label class="form-label mt-5" for="numberOfSeats">Number of Seats</label>
  <div class="center input-group">

    <input formControlName="number" type="number" class="form-control" id="numberOfSeats" name="numberOfSeats"
           placeholder="Number of Seats" />
    <button type="submit" class="btn btn-primary">
      <i class="fas fa-ticket"></i>
      Book
    </button>
  </div>
  </form>

</div>
